package com.med.sql;/** * This class implements the MSSQL-specific features of a JdbcProperties * @author TeamSQL */public class MssqlJdbcProperties extends AbstractJdbcProperties {	protected Class getDriverClass() throws ClassNotFoundException {		final String driverClassName = "net.sourceforge.jtds.jdbc.Driver";		return Class.forName(driverClassName);	}	protected JdbcUrl createJdbcUrl() {		return new MssqlJdbcUrl();	}}